import React, { Component } from 'react';
import Header from './Components/Header.js'
import Container from './Components/Container.js';
import Card from './Components/Card.js';
import data from './data.js'

import './App.scss';


export class App extends Component {
  constructor() {
    super();
    this.state = {
      questions: data,
      completed: [],
      selection: []

    }
  }

  showAll(){

  }

  addCompleted = (ID) => {
    let newState;
    if (this.state.completed.includes(ID)) {
      newState = this.state.completed.filter(card => card !== ID);
    } else {
      console.log(ID)
      newState = [...this.state.completed, ID];
    }
    this.setState({
      completed : newState
    }, () => {
      this.saveToStorage();
    });
    console.log("completed in app:", this.state.completed)
  }

  saveToStorage = () => {
    localStorage.setItem('Completed', JSON.stringify(this.state.completed));
    console.log(this.state.completed)
  }

  componentDidMount() {
    const completedCards = JSON.parse(localStorage.getItem('Completed')) || [];
      this.setState({
        completed: completedCards,
        selection: data.flashCards
      });
      console.log(this.state.selection)

  }

  render() {
      return(
        <div>
          <Header/>
          <div className="button-holder">
            <button>All</button>
            <button>Easy</button>
            <button>Medium</button>
            <button>Hard</button>
            <button>Completed</button>
          </div>
          <hr/>
          <Container 
          data = {this.state.questions}
          completed = {this.state.completed}
          addCompleted = {this.addCompleted}
          />
      </div>
      );
  }

}

export default App;
