import React, { Component } from 'react';
import CompleteButton from './CompleteButton.js'
import complete from '../Images/completed.svg'

class Card extends Component{
  constructor(props){
    super(props);
    this.state = {
    answer: null,
    revealAnswer: false

    }
  }

  checkAnswer = (e) =>{
    console.log(this.props.Answer)
    if(this.state.answer = this.props.Answer){
      console.log("Hooray")
    }else{
      console.log("Nope")
    }
    console.log("Click:", e.target)
    console.log(this.props.ID)
  }

  recordResponse = (e) =>{
    let response = e.target.value;
    console.log(response)
    this.setState({answer: response})
    console.log(this.state.answer)

  }
  render(){
    return (
      <div className="card">
        <div className="question-area">
          <h2> {this.props.Question} </h2>
        </div>
        <div className="input-area">
          <input id= {this.props.ID} onChange={this.recordResponse}/>
          <br/>
          <button className="submit" onClick ={this.checkAnswer}> Check Answer </button>
        </div>
        <div className="status">
          <h6> {this.props.Difficulty} </h6>
          <CompleteButton
          status= {complete} />
        </div>
      </div>
      );

  }



}


export default Card;
